<DOCTYPE html><html lang="de-de" xmlns="http://www.w3.org/1999/xhtml" xmlns:fb="http://ogp.me/ns/fb#"><head>	<meta charset="utf-8">	<title>Hello Facebook JS-SDK!</title></head><body><h1>JavaScript-SDK: Payment-Dialog</h1><a href="#" onclick="fbPay();">fbPay();</a><br/><a href="#" onclick="fbEarn();">fbEarn();</a><br/><div id="fb-root"></div><script>var fbPay = function() {   var order_info = 'produkt-1';   var obj = {          method: 'pay',          order_info: order_info,          purchase_type: 'item',          dev_purchase_params: {'oscif': false}        };           FB.ui(obj, function (data) {      console.debug(data);   });}var fbEarn = function() {   var order_info = 'produkt-1';   var obj = {          method: 'pay',          action: 'earn_credits'        };           FB.ui(obj, function (data) {      console.debug(data);   });}              window.fbAsyncInit = function() {   FB.init({      appId      : '214728715257742',                    // Anwendungs-ID      channelUrl : '//apps.mycompany.com/channel.php',                    // Channel-Datei      status     : true, // Login Status pr√ºfen      cookie     : true, // Cookies aktivieren      oauth      : true, // OAuth 2.0 aktivieren      xfbml      : true  // XFBML parsen   });         };// Asynchrones Laden der SDK(function(d){ var js,id = 'facebook-jssdk'; if (d.getElementById(id)) {return;} js = d.createElement('script'); js.id = id; js.async = true; js.src = "//connect.facebook.net/en_US/all.js"; d.getElementsByTagName('head')[0].appendChild(js);}(document));</script></body></html>