<DOCTYPE html><html lang="de-de" xmlns="http://www.w3.org/1999/xhtml" xmlns:fb="http://ogp.me/ns/fb#"><head>	<meta charset="utf-8">	<title>Hello Facebook JS-SDK!</title></head><body><h1>FB.ui</h1><h2>Send-Dialog</h2><p>	<a href="#" onclick="fbUiSend();">fbUiSend();</a><br/></p><div id="fb-root"></div><script>window.fbAsyncInit = function() {    FB.init({      appId      : '214728715257742',                    // Anwendungs-ID      channelUrl : '//apps.mycompany.com/channel.php',                    // Channel-Datei      status     : true, // Login Status pr√ºfen      cookie     : true, // Cookies aktivieren      oauth      : true, // OAuth 2.0 aktivieren      xfbml      : true  // XFBML parsen    });    };var fbUiSend = function() {   var options = {      method: 'send',      to: '532617296,633616172',      link: 'http://die.socialisten.at/'     };           FB.ui(options, function(result) {       console.debug(result);   });   };// Asynchrones Laden der SDK(function(d){ var js,id = 'facebook-jssdk'; if (d.getElementById(id)) {return;} js = d.createElement('script'); js.id = id; js.async = true; js.src = "//connect.facebook.net/en_US/all.js"; d.getElementsByTagName('head')[0].appendChild(js);}(document));</script></body></html>