<DOCTYPE html><!-- Facebook Programmierung - Galileo Verlag, Michael Kamleitner     Weitere Informationen sowie Aktualisierungen zu diesem Code-Beispiel finden Sie unter http://book.socialisten.at  --><html lang="de-de" xmlns="http://www.w3.org/1999/xhtml" xmlns:fb="http://ogp.me/ns/fb#"><head>	<meta charset="utf-8">	<title>Hello Facebook JS-SDK!</title></head><body><h1>Social Plugins</h1><h2>Subscribe-Button</h2><p>Layout: standard</p><div class="fb-subscribe" data-href="https://www.facebook.com/zuck" data-layout="standard" data-show-faces="true" data-width="450"></div><hr/><p>Layout: box_count</p><div class="fb-subscribe" data-href="https://www.facebook.com/zuck" data-layout="box_count" data-show-faces="true" data-width="450"></div><hr/><p>Layout: button_count</p><div class="fb-subscribe" data-href="https://www.facebook.com/zuck" data-layout="button_count" data-show-faces="true" data-width="450"></div><hr/><div id="fb-root"></div><script>window.fbAsyncInit = function() {    FB.init({      appId      : '214728715257742',                    // Anwendungs-ID      channelUrl : '//apps.mycompany.com/channel.php',                    // Channel-Datei      status     : true, // Login Status pr√ºfen      cookie     : true, // Cookies aktivieren      oauth      : true, // OAuth 2.0 aktivieren      xfbml      : true  // XFBML parsen    });    // Weitere Initalisierungen hier vornehmen...        FB.Event.subscribe('message.send', function(response) {    	console.debug('message.send');    	console.debug(response);    });    message.send};// Asynchrones Laden der SDK(function(d){ var js,id = 'facebook-jssdk'; if (d.getElementById(id)) {return;} js = d.createElement('script'); js.id = id; js.async = true; js.src = "//connect.facebook.net/en_US/all.js"; d.getElementsByTagName('head')[0].appendChild(js);}(document));</script></body></html>